mapper.select:
		select * from notice where
		ifnull(deleteyn,'N')='N'
		order by regidate desc limit 0,5;

		select * from notice where
		ifnull(deleteyn,'N')='N' and category = 'SM'
		order by regidate desc limit 0,5;;

		select * from notice where
		ifnull(deleteyn,'N')='N' and category = 'EV'
		order by regidate desc limit 0,5;

		select * from notice where
		ifnull(deleteyn,'N')='Y'
		order by regidate desc limit 0,5;

mapper.update:
update notice
set deleteyn = 'Y'
where notCD= #{notCD} ;

mapper.select:
		select * from notice where
		ifnull(deleteyn,'N')='N'
		
			and category = #{category}
		
		order by regidate desc limit ${row}, 5;

		select b.* from (
		SELECT
		@rnum:=@rnum + 1 as rownum, a.*
		FROM
		notice a,
		(select
		@rnum:=0) r
		) b
		where b.rownum between ${startrow} and ${endrow};
